import { EventEmitter } from "eventemitter3";
import {
  WalletError,
  WalletNotFoundError,
  WalletConnectionError,
  WalletNotSelectedError,
  WalletDisconnectedError,
  WalletSignMessageError,
  WalletSignTransactionError,
  WalletSwitchChainError,
  AdapterName
} from "@hadron-connect/abstract-adapter"; // adjust this path as needed
import { AdapterState, NetworkType, TronLinkMessageData, TronProvider, WalletReadyState, WatchAssetParams } from "./types";
import { getNetworkInfoByTronWeb } from "./utils";

/** Extend Window interface for TS */
declare global {
  interface Window {
    tron?: TronProvider;
    tronLink?: TronProvider;
  }
}


export const TronLinkAdapterName = 'TronLink' as AdapterName<'TronLink'>;

export class TronLinkAdapter extends EventEmitter {
  protected provider?: TronProvider;
  public readonly name = TronLinkAdapterName;
  public readonly url: string = 'https://www.tronlink.org/';
  public readonly icon:string = 'data:image/png;base64,';
  public address?: string;
  public chainId?: string;
  public network?: NetworkType;
  public connected = false;

  public adapterState: AdapterState = AdapterState.Loading;
  public readyState: WalletReadyState = WalletReadyState.Loading;

  private readonly messageHandler = this.handlePostMessage.bind(this);

  constructor() {
    super();
    this.waitForProvider();
    if (typeof window !== "undefined") {
      window.addEventListener("message", this.messageHandler as EventListener);
    }
  }

  private async waitForProvider(retries = 10, delay = 300): Promise<void> {
    for (let i = 0; i < retries; i++) {
      const w = typeof window !== "undefined" ? window : undefined;
      if (w?.tron || w?.tronLink) {
        this.provider = w.tron ??  w.tronLink;
        console.log("this.provider = w.tron ?? w.tronLink;", w.tron,w.tronLink)
        this.readyState = WalletReadyState.Found;
        this.adapterState = AdapterState.Disconnect;
        if (!w.tron && w.tronLink) {
          console.warn("⚠️  Falling back to window.tronLink — some TIP‑1193 features may be missing.");
        }
        return;
      }
      await new Promise((resolve) => setTimeout(resolve, delay));
    }
    this.readyState = WalletReadyState.NotFound;
    this.adapterState = AdapterState.NotFound;
    throw new WalletNotFoundError("Tron provider not found after waiting");
  }

  disconnect(): void {
    if (typeof window !== "undefined") {
      window.removeEventListener("message", this.messageHandler as EventListener);
    }
    this.address = this.chainId = undefined;
    this.network = undefined;
    this.connected = false;
    this.adapterState = AdapterState.Disconnect;
    this.emit("disconnect");
  }

  async connect(): Promise<string> {
    this.assertProvider();
    try {
      console.log("this.provider---------------->",this.provider)
      const res = await this.provider.request({ method: "tron_requestAccounts" });
      console.log(res)
      const accounts = (await this.provider.request({ method: "tron_accounts" })) as string[];
      console.log(accounts)
      if (!accounts?.length) throw new WalletNotSelectedError("No Tron account returned");
      this.address = accounts[0];
      const { chainId, networkType } = await this.getChainInfo();
      this.chainId = chainId;
      this.network = networkType;
      this.connected = true;
      this.adapterState = AdapterState.Connected;
      this.emit("connect", this.address);
      return this.address;
    } catch (err) {
      console.log(err)
      const err_message = err && typeof err === "object" && "message" in err ? (err as Error).message : "Failed to connect to TronLink wallet";
      throw new WalletConnectionError(err_message, err);
    }
  }

  async signMessage(message: string): Promise<string> {
    this.assertProvider();
    try {
      return await this.provider.tronWeb.trx.sign(message);
    } catch (err) {
      throw new WalletSignMessageError("Failed to sign message", err);
    }
  }

  async sendTransaction(rawTx: object): Promise<string> {
    this.assertProvider();
    try {
      const signed = await this.provider.tronWeb.trx.sign(rawTx);
      const res = await this.provider.tronWeb.trx.sendRawTransaction(signed);
      if (!res?.txid) throw new WalletSignTransactionError("Transaction failed to send", res);
      return res.txid;
    } catch (err) {
      throw new WalletSignTransactionError("Failed to send transaction", err);
    }
  }

  async switchChain(chainId: string): Promise<void> {
    this.assertProvider();
    try {
      await this.provider.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId }],
      });
    } catch (err) {
      throw new WalletSwitchChainError("Failed to switch chain", err);
    }
  }

  async watchAsset(params: WatchAssetParams): Promise<void> {
    this.assertProvider();
    if (!this.provider.tronWeb.request) {
      throw new WalletError("tronWeb.request is unavailable on this provider");
    }
    try {
      await this.provider.request({
        method: "wallet_watchAsset",
        params,
      });
    } catch (err) {
      throw new WalletError("Failed to watch asset", err);
    }
  }

  async getChainInfo(): Promise<{ chainId: string; networkType: NetworkType }> {
    this.assertProvider();
    return getNetworkInfoByTronWeb(this.provider.tronWeb);
  }

  async getChainId(): Promise<string> {
    return (await this.getChainInfo()).chainId;
  }

  getProvider(): TronProvider {
    this.assertProvider();
    return this.provider;
  }

  private assertProvider(): asserts this is { provider: TronProvider } {
    if (!this.provider) {
      throw new WalletDisconnectedError("Tron provider not injected (window.tron or window.tronLink missing)");
    }
  }

  private handlePostMessage(evt: MessageEvent<TronLinkMessageData>): void {
    const { isTronLink, message } = evt.data;
    if (!isTronLink || !message?.action) return;

    switch (message.action) {
      case "accountsChanged":
        this.address = (message.data as string[])?.[0] ?? this.address;
        this.emit("accountsChanged", this.address);
        break;
      case "connect":
        this.connected = true;
        this.adapterState = AdapterState.Connected;
        this.emit("connect", this.address);
        break;
      case "disconnect":
        this.disconnect();
        break;
      case "setNode":
        this.getChainInfo().then(({ chainId, networkType }) => {
          this.chainId = chainId;
          this.network = networkType;
          this.emit("chainChanged", { chainId, networkType });
        });
        break;
    }
  }
}
